INSERT INTO games (id, name, summary, release) VALUES (9583, '0rbitalis', '0rbitalis is a satellite launching simulator with a retro-puzzle style. Launch your satellite into orbit and go head to head with the dominant force that formed the entire universe: Gravity.
INSERT INTO games (id, name, summary, release) VALUES (1898, '0x10c', 'In a parallel universe where the space race never ended, space travel was gaining popularity amongst corporations and rich individuals.', null) ON CONFLICT DO NOTHING;
