INSERT INTO games (id, name, summary, release) VALUES (17331, '8BitBoy', 'Big classic platformer with tons of power-ups and 12+ hours of main story & 300+ hidden locations just begging for exploration. 8BitBoy is a tribute to all the greatest platformers of the 80s and 90s and a symbol of my endless love and passion for gaming.', DATE('2014-03-31')) ON CONFLICT DO NOTHING;
