INSERT INTO games (id, name, summary, release) VALUES (17143, '9 Clues: The Secret of Serpent Creek', '9 Clues: The Secret of Serpent Creek is an original hidden-object puzzle-adventure game, featuring a totally unique Detective Mode. This thrilling mystery merges elements of classical horror and gritty, 50’s-style noir. 
INSERT INTO games (id, name, summary, release) VALUES (11879, '9 Lives', 'Bob Cat has a girl in every town, but there is one who''s special to his heart; Claudette cat. Claudette has the biggest eyes you''ve ever seen and Bob aims to make her his queen.
INSERT INTO games (id, name, summary, release) VALUES (16837, '9.03m', '9.03m, is a short, first person, art/empathy game for PC. Not a game in the traditional sense of the word; it aims to humanise, and remember the victims of the 2011 Japanese tsunami. The media is quick to put figures to death tolls in such disasters, and 9.03m tries to remind people of the individuals behind those figures.', DATE('2013-10-02')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3707, '90 Minutes', 'Players have the freedom to select from over 32 national teams, as well as club teams from major leagues across Europe and around the world. There is a choice of club teams from the English, French, German, Italian and Spanish leagues. Players may challenge for the World Championship (based on the World Cup) or compete for title glory in a Domestic League competition. The game also features in-game commentary from British television and radio commentator Alan Parry.', DATE('2001-10-24')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20927, '911 Operator', '911 Operator is a game about the difficult work of people that manage emergency lines and services. Main task is to answer incoming calls and to react properly - give first aid instructions, advise, dispatch correct number of firemen / police / ambulances, or sometimes just ignore the call.', DATE('2017-02-23')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11436, '911: First Responders', 'In 911: First Responders, become the head of operations in a fictional rescue and catastrophe management organization. Command a number of vehicles and staff from the fire department, medical rescue, police and technical services. Be a real world hero set against a background of continuous day/night changes and constantly varying weather conditions.', DATE('2007-06-21')) ON CONFLICT DO NOTHING;
