INSERT INTO games (id, name, summary, release) VALUES (2109, 'A Boy and His Blob', 'A Boy and His Blob: Trouble on Blobolonia is a 1989 video game developed by Imagineering for the Nintendo Entertainment System (NES). The game was published by Absolute Entertainment in North America and Europe and by Jaleco in Japan. A Boy and His Blob follows an unnamed, male protagonist and his shapeshifting blob friend on their adventure to save the planet of Blobolonia from the clutches of an evil emperor.', DATE('1989-11-30')) ON CONFLICT DO NOTHING;
