INSERT INTO games (id, name, summary, release) VALUES (18653, 'A Place for the Unwilling', '“A Place for the Unwilling” is a narrative adventure game where the city is the actual protagonist. No puzzles or “game over” screens, just you, the city and the dozens of stories which are taking place in there. Days pass and the fate of the city is yet to be decided.', null) ON CONFLICT DO NOTHING;
