INSERT INTO games (id, name, summary, release) VALUES (20756, 'Adam''s Venture Chronicles', 'Help renowned explorer and adventurer Adam Venture and his accomplice Evelyn discover artifacts, unravel ancient mysteries, decipher strange symbols and solve ingenious puzzles around gorgeous historical sites from the 1920''s.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8181, 'Adam''s Venture Episode 1: The Search For The Lost Garden', 'Set during the 1920’s, Adam’s Venture delivers a unique combination of action oriented gameplay with classic puzzle solving, that takes you on an epic journey to the middle East to discover the legendary Lost Garden of Eden – but an ancient evil tries to prevent you from succeeding... 
INSERT INTO games (id, name, summary, release) VALUES (8182, 'Adam''s Venture Episode 2: Solomon''s Secret', 'Adam Venture is an action explorer hero; adventurous but using his wits rather than resorting to violence. Brought to life from a third person perspective with the Unreal 3™ engine, adventure gamers are in for a real visual treat in combination with family values storytelling. 
INSERT INTO games (id, name, summary, release) VALUES (8183, 'Adam''s Venture Episode 3: Revelations', 'Adam’s Venture is an adventure game franchise that is focused on non-violent gameplay set in the early twenties of the 20th Century. With state of the art Unreal 3 graphics providing colorful and lifelike 3D environments, a gripping storyline and many puzzles to solve varying from easy to difficult, Adam’s Venture supplies everything that an adventure gamer is looking for. &#13;
INSERT INTO games (id, name, summary, release) VALUES (16298, 'Adam''s Venture: Origins', 'Embark on a bold adventure in Adam’s Venture: Origins. Set in the roaring 20’s, in Adam’s Venture: Origins you will explore ancient ruins, and recover mysterious artifacts. Together with your trusted accomplice Evelyn, you will have to outsmart the evil Clairvaux company.', DATE('2016-03-31')) ON CONFLICT DO NOTHING;
