INSERT INTO games (id, name, summary, release) VALUES (8602, 'Advanced Dungeons & Dragons: Dragons of flame', 'This game is a sequel to Heroes of the Lance. It is arcade oriented, with RPG elements. The player utilizes both a top-down view of map travelling, as well as side-view scrolling combat.', DATE('1989-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8603, 'Advanced Dungeons & Dragons: Heroes of the Lance', 'Heroes of the Lance is a side-scrolling action game. The game used actual Dungeons &amp; Dragons statistics, with statistics for the characters exactly as they were in the rule books. Eight heroes from the Dragonlance novels series must be assembled for the quest, and only one is visible on the screen at a time; when one on-screen hero dies, the next in line appears.', DATE('1987-12-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8622, 'Advanced Dungeons & Dragons: Hillsfar', 'Hillsfar is a role-playing video game released for MS-DOS, Amiga, Atari ST, Commodore 64 in 1989, and NES in 1993. It features a combination of real-time action and randomly generated quests. It also includes standard gameplay elements of the Advanced Dungeons &amp; Dragons fantasy role-playing game, upon which the game is based.', DATE('1989-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8732, 'Advanced Dungeons & Dragons: Pool of Radiance', 'Pool of Radiance was the first adaptation of TSR''s Advanced Dungeons &amp; Dragons (AD&amp;D or D&amp;D) fantasy role-playing game for home computers, becoming the first episode in a four-part series of D&amp;D computer adventure games. The player starts by building a party of up to six characters, deciding the race, sex, class and ability scores for each.', DATE('1988-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5655, 'Advanced Dungeons & Dragons: Treasure of Tarmin', 'In Treasure of Tarmin the player wanders through a multi-tiered dungeon, each level consisting of a maze and its surrounding hallway. The objective is to slay the Minotaur who guards the Treasure of Tarmin and take his treasure.', DATE('1983-12-30')) ON CONFLICT DO NOTHING;
