INSERT INTO games (id, name, summary, release) VALUES (11441, 'Adventure Chronicles: The Search For Lost Treasure', 'After narrowly saving and escaping an earthquake struck Museum with her daughter, Susan Anderson is presented with an unbelievably rare opportunity to travel the world, and explore never before seen treasures. Can you guide Susan on her quest to find some of the greatest unsolved treasures in history.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2416, 'Adventure Construction Set', 'Play 8 great adventures... Or write your own.
INSERT INTO games (id, name, summary, release) VALUES (4594, 'Adventure Island', 'Hudson''s Adventure Island, also known simply as Adventure Island and released in Japan as Takahashi Meijin no Bōken Jima (高橋名人の冒険島 lit. "Master Takahashi''s Adventure Island"), is a side-scrolling platform game produced by Hudson Soft that was first released in Japan for the Famicom and MSX on September 12, 1986. It was later released in North America for the Nintendo Entertainment System on September 1988 and in the PAL region in 1992 under the title of Adventure Island Classic.
INSERT INTO games (id, name, summary, release) VALUES (6471, 'Adventure Island II', 'Adventure Island II is a side-scrolling platform game developed by Now Production and published by Hudson Soft that was released for the Nintendo Entertainment System in 1991. It is the sequel to Adventure Island and the second game in the series for the NES. While the first game was an adaptation of Westone''s Wonder Boy arcade game, Adventure Island II is an entirely original work, as were all the subsequent Adventure Island games.', DATE('1991-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8737, 'Adventure Island III', 'Adventure Island is a side-scrolling platform game published by Hudson Soft that was originally released for the Nintendo Entertainment System in 1992. It is the third game in the Adventure Island released for the NES, following Adventure Island II.', DATE('1992-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24040, 'Adventure Lamp', 'A cave-in has separated Simon, the highly prepared archaeologist, from his equipment. With nothing but a head lamp and his wits, Simon would very much like to stay alive long enough to return his notes to The Academy for study.', DATE('2016-07-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9947, 'Adventure Park', 'Build your own amusement park, according to your personal wishes and ideas. There are no limits set to your imagination! Attract visitors to your theme park with different fancy and thrilling attractions. The money from and the reactions of the visitors will help you enhance your park and make it even more attractive.', null) ON CONFLICT DO NOTHING;
