INSERT INTO games (id, name, summary, release) VALUES (4557, 'Adventure Time: Explore the Dungeon Because I Don''t Know!', 'Princess Bubblegum summons Finn and Jake to the Candy Kingdom to investigate a rash of thefts and kidnappings that have plagued her kingdom. She thinks the cause lies within her Secret Royal Dungeon, where only the nastiest monsters and criminals are locked up.', DATE('2013-11-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11320, 'Adventure Time: Finn and Jake Investigations', 'Adventure Time: Finn &amp; Jake Investigations is an upcoming action adventure video game developed by Vicious Cycle Software under license from Cartoon Network Interactive. It will be published by Little Orbit for Microsoft Windows, PlayStation 3, PlayStation 4, Xbox 360, Xbox One, Wii U, and Nintendo 3DS. This is the fourth game based on the animated television series Adventure Time following The Secret of the Nameless Kingdom, and the first action adventure title in the series presented in full 3D graphics. The game was first announced on April 21, 2015, and will be released on October 2015.
