INSERT INTO games (id, name, summary, release) VALUES (11442, 'Adventurer Manager', 'The evil Illusionist Miraj has murdered your parents, the King and Queen, and has taken control of the Kingdom of Adventuria. Playing as the King''s sole heir, you must recruit local Adventurers to complete Adventures to regain the loyalty of the various races in the realm. Only through reuniting your kingdom will you have the power to defeat the evil Illusionist Miraj!', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8193, 'Adventurer''s Consumer Guide', 'Ever wondered where the items of videogames come from? After this (literally) experimental text adventure you know.', DATE('2007-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14225, 'Adventures in Math', null, DATE('1991-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (23739, 'Adventures in the Galaxy of Fantabulous Wonderment', 'Adventures in the Galaxy of Fantabulous Wonderment is a humorous adventure game following unwilling earthling Daniel Gordon in his adventures across space.  The game features a mix of point-and-click adventure gameplay, a trading simulator, turn-based space battles, several minigames, and the odd ethical conundrum.', DATE('2005-07-26')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8130, 'Adventures in the Magic Kingdom', 'Adventures in the Magic Kingdom is a game developed by Capcon where the player explores various rides in Disney theme parks. The player must complete five different stages, modeled after rides, as well as a trivia quest in order to retrieve the set of keys. Players choose which stage to play by walking around the park in the game''s overworld map.', DATE('1990-05-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8741, 'Adventures of Dino Riki', 'Adventures of Dino Riki is an overhead shooter created by Hudson Soft, similar to games like 1943: The Battle of Midway, where the object is to dodge on-screen enemies. Dino Riki can jump or shoot weapons (rocks, axes, boomerangs, and torches) to kill the various enemies around him. Dino Riki can pick up power-ups including speed boosts and wings that enable him to fly over hazards on the ground, as well as weapon upgrades.', DATE('1987-02-09')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3662, 'Adventures of Lolo', 'Adventures of Lolo is a puzzle game released in 1989 by HAL Corporation for the Nintendo Entertainment System. It is based on the Japanese Eggerland video game series.', DATE('1989-04-19')) ON CONFLICT DO NOTHING;
