INSERT INTO games (id, name, summary, release) VALUES (25663, 'Adventuring Gentleman', 'Adventuring Gentleman is a steampunk roguelite platform shooter where you can go on a number of adventures offered by your clubhouse members. Each adventure consists of proceduraly generated floor with enemy wave sequences unique to the chosen adventure. Each adventure has three difficulty levels and lengths. After beating each floor you are offered a choice from two power ups which will make you stronger, change play style and alter your dashing looks. Depending on the length of an adventure you will face up to three boss battles.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2601, 'Aegis Wing', 'Ready to save the universe from an Araxian invasion? Challenge them solo or recruit up to three friends for an all-out assault. Feeling lucky? Try it on Insane mode – the rewards are greater but the name is all too accurate.', DATE('2007-05-15')) ON CONFLICT DO NOTHING;
