INSERT INTO games (id, name, summary, release) VALUES (7268, 'Akiba''s Trip: Undead & Undressed', 'Akiba''s Trip: Undead &amp; Undressed is an open-world action RPG in which players undertake numerous missions in a virtual recreation of Akihabara, Tokyo’s popular ”Electric Town” district.', DATE('2013-11-06')) ON CONFLICT DO NOTHING;
