INSERT INTO games (id, name, summary, release) VALUES (4145, 'Akuji the Heartless', 'Akuji the Heartless is an action-adventure video game, released in 1998 on the PlayStation. It uses a modified Gex: Enter the Gecko engine, and both games were developed by Crystal Dynamics. The gameplay involves hack and slash combat combined with platform elements.', DATE('1998-12-30')) ON CONFLICT DO NOTHING;
