INSERT INTO games (id, name, summary, release) VALUES (7600, 'Alienation', 'Isometric shooter set in the fantasy/science-fiction, which is the spiritual successor of similar conceptually, Dead Nation. The player takes the role of a mercenary who fights against all the hordes of invading aliens. In addition, the Alienation offers an optional cooperative mode for four people.', DATE('2016-04-25')) ON CONFLICT DO NOTHING;
