INSERT INTO games (id, name, summary, release) VALUES (13157, 'Allods Online', 'Allods Online is a free-to-play 3D fantasy MMORPG developed by Allods Team, formerly Astrum Nival, and published by Mail.Ru Group.', DATE('2010-10-20')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9970, 'Alltynex Second', 'The year is 2192AD. 40 years ago, the Stellar-class All-purpose Administrative Computer ALLTYNEX suddenly attempted to enact genocide upon mankind. Commandeering armies of unmanned weapons, ALLTYNEX enacted a massacre so terrible that within only 72 hours of the start of the incident, approximately 85% of the world''s population had perished.
