INSERT INTO games (id, name, summary, release) VALUES (596, 'American Conquest', 'American Conquest: In the year 1492, travel to America as Christopher Columbus and ignite the fire that will fan 300 years of heated battles for land, riches, and freedom. In eight historical campaigns spanning 42 missions, you will take on the roles of great legends such as Francisco Pizarro and George Washington. Secure new technologies, unique weaponry, and improved fighting units, but also master the delicate world of diplomacy. Play with fire! Conquer America.', DATE('2003-02-04')) ON CONFLICT DO NOTHING;
