INSERT INTO games (id, name, summary, release) VALUES (54, 'An Elder Scrolls Legend: Battlespire', 'An Elder Scrolls Legend: Battlespire is an action role-playing open world video game developed and published by Bethesda Softworks, set in the world of The Elder Scrolls.', DATE('1997-11-29')) ON CONFLICT DO NOTHING;
