INSERT INTO games (id, name, summary, release) VALUES (4592, 'Antarctic Adventure', 'Antarctic Adventure, known in Japan as Kekkyoku Nankyoku Daibōken (けっきょく南極大冒険) is a video game developed by Konami in 1983 for MSX, and later for video game consoles, such as NES. The player takes the role of an Antarctic penguin, racing to disparate research stations owned by different countries in Antarctica.
