INSERT INTO games (id, name, summary, release) VALUES (27719, 'Ape Out', 'Ape Out is a wildly intense and colorfully stylized smash ‘em up about primal escape, rhythmic violence, and frenetic jazz. Build up nearly unstoppable momentum and use your captors as both weapons and shields to crush everyone on your procedurally generated path to freedom.', DATE('2017-09-29')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20906, 'Apeiron', 'An action-packed arcade game based on the classic shoot-em-up, Centipede, but with engaging, colorful animations and fun, wacky sounds.  It was released exclusively for the Mac operating system by the indie developer, Ambrosia Software.', DATE('1995-01-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14385, 'Aperion Cyberstorm', 'Aperion Cyberstorm is a 1-5 player 2D twin-stick shooter for Wii U, where combining your tools can save you, or scrap you. Salvage what’s left of your team against dangers in- and outside of the galaxy, and mix the abilities you find before you’re added to the heap; smash an enemy’s shields with electricity-imbued mines, before melting their hull with a spread of fire rockets.', DATE('2017-03-30')) ON CONFLICT DO NOTHING;
