INSERT INTO games (id, name, summary, release) VALUES (18868, 'Arcadian Atlas', 'Arcadian Atlas is a 2D isometric tactical RPG for Mac/PC/iOS/Linux about the choices people make in pursuit of the things they love, and the havoc it wreaks on a kingdom.
