INSERT INTO games (id, name, summary, release) VALUES (15864, 'Arcana Heart', 'A 2D fighter bursting with brawling anime-style beauties, for the PlayStation 2 computer entertainment system. These gorgeous fighters will make your jaw drop with their looks (or their fists). This April 8th, beauty is in the black eye of the beholder.', null) ON CONFLICT DO NOTHING;
