INSERT INTO games (id, name, summary, release) VALUES (4515, 'Arch Rivals', 'Arch Rivals is a basketball game developed and published by Midway in 1989. The game introduces violence as an important part of the game, encouraging players to punch opposing players and stealing the ball from them. The game is regarded as the forerunner to Midway''s popular arcade basketball game, NBA Jam.', DATE('1989-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (20598, 'ArchLord', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25625, 'Archaica: The Path Of Light', 'Archaica: The Path Of Light is a visually stunning puzzle game with lasers and mirrors, that enraptures gamers not only with finely designed levels, but also with an interesting storyline. In this game we become the Chosen One from the nation that believes walking the legendary Path of Light will save the world from impending disaster. We revive ancient mechanisms, explore secrets of an ancient civilization, and discover the true destiny of The Path along the way through extraordinary realms.', DATE('2017-03-30')) ON CONFLICT DO NOTHING;
