INSERT INTO games (id, name, summary, release) VALUES (11664, 'ArcheBlade', 'ArcheBlade is a free-to-play PvP-based multiplayer action game in which users can either take sides and fight against the other team or fight for themselves depending on the map.', DATE('2014-04-23')) ON CONFLICT DO NOTHING;
