INSERT INTO games (id, name, summary, release) VALUES (12256, 'Argos', null, DATE('1983-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17660, 'Aritana and the Harpy''s Feather', 'Aritana is a platform game which the main aspect of its mechanics is the possibility of changing your stance. This stances define attributes such Speed, Jump Force, Attacks and Special Abilities. The player must choose between these stances to overcome obstacles and fight the Spirits of the Forest in a quick, intuitive and fun gameplay mechanic.', DATE('2014-07-04')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18303, 'Arizona Sunshine', 'Step into the midst of a zombie apocalypse as if you were really there, and bash, slash and explode your way through hordes of undead close enough to touch. Arizona Sunshine has you battle for survival across a blistering hot, post-apocalyptic Arizona. Brought (back) to life with Steam VR on HTC Vive.
