INSERT INTO games (id, name, summary, release) VALUES (21064, 'Assault Suits Velkan', 'Assault Suits Velkan is a port of the 1993 SNES game of the same name, known as Cybernator in the west. Was released in Europe and Japan, but not in the US. It added wide screen grafics and some new cutscenes and dialogue.', DATE('2004-06-30')) ON CONFLICT DO NOTHING;
