INSERT INTO games (id, name, summary, release) VALUES (16760, 'Atari Football', 'Atari Football is a 2-player 1978 arcade game in which, the sport of American football is accurately emulated, with players represented by Xs and Os. The game was one of the most popular arcade games in its day and is credited with popularizing the trackball. Twenty-five cents would allow 90 seconds of playtime, while adding more quarters would allow longer play. Considered physically exhausting to play, Atari Football involves spinning the trackball as fast as possible to win the game. Just 90 seconds of play could result in sore palms, and longer could cause blisters.', DATE('1978-09-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18437, 'Atari Greatest Hits Volume 1', 'The Atari Greatest Hits series is composed of two compilations of retro Atari arcade games &amp; Atari 2600 games ported to the Nintendo DS.', DATE('2010-11-01')) ON CONFLICT DO NOTHING;
