INSERT INTO games (id, name, summary, release) VALUES (11645, 'Attrition Nuclear Domination', 'Attrition: Nuclear Domination is a 3d missile command style game versus up to nine other AI opponents. The game features resource collection, nuclear, and nuclear suppression devices. The player can choose to be any of the ten available nations.', DATE('2015-08-06')) ON CONFLICT DO NOTHING;
