INSERT INTO games (id, name, summary, release) VALUES (18981, 'Audioshield', 'Block the beats! Audioshield puts you at the point of impact for every hit in your songs. Block incoming orbs with your shields and feel the music. Works with any song file, plus online music streaming and Song of the Day.', DATE('2016-04-04')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2956, 'Audiosurf', 'Audiosurf is a puzzle/rhythm hybrid game created by Invisible Handlebar, a personal company created by Dylan Fitterer. It''s track-like stages visually mimic the music the player chooses, while the player races across several lanes collecting colored blocks that appear in sync with the music. The music used in the game is chosen from the user''s own library, from almost any DRM-free format, as well as standard redbook CDs; in addition, Audiosurf includes the entire soundtrack to The Orange Box as part of the download.', DATE('2008-02-14')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9707, 'Audiosurf 2', 'By blending gameplay with music visualization, Audiosurf puts you inside your music in a way nothing else can. Use your own music to create your own experience. The shape, the speed, and the mood of each ride is determined by the song you choose.', DATE('2015-05-25')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9555, 'Audition Online', 'Audition Online (Korean: 오디션 온라인), also known as X-BEAT in Japan, popularly called AyoDance in Indonesia, is a downloadable multiplayer online casual rhythm game produced by T3 Entertainment. It was originally released in South Korea in 2004, but it has been localized by various publishers around the world. Audition Online is free to play but it earns its revenue by selling virtual items such as clothes for the player''s avatar.
INSERT INTO games (id, name, summary, release) VALUES (15282, 'Auditorium', 'The classic particle puzzler finally arrives on Steam! Turn light into sound in this multi-award winning game. Auditorium is about the process of discovery and play. There are no right or wrong answers, and there are any number of ways to solve each puzzle. See why Auditorium has won countless awards and is considered the Second Best Browser Game of All Time.', DATE('2012-01-01')) ON CONFLICT DO NOTHING;
