INSERT INTO games (id, name, summary, release) VALUES (14457, 'Autobots', 'Clear levels by making robots smash into one another. You do this by manipulating them to crash into one another using your player icon (the robots always move towards your player, so if you can get two robots moving on the same lateral plane towards you then they are an easy kill.) When two robots crash into one another they form a pile of debris which can also be used to destroy robots.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17178, 'Autocraft', 'Create, Collect, and Crash in Autocraft, a physics destruction puzzle/sandbox from the creators of Apotheon and Capsized.', DATE('2014-09-02')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13353, 'Autocrash', 'In Autocrash the player competes in a death tournament between "bumper cars". The goal is to dismount the opponent (or opponents) from his car, and hit him before he leaves the game zone. Along the stages, the enemy''s A.I. difficulty level and his number will increase. Additionally, in some stages the enemies must be eliminated in a certain order.', DATE('1991-12-30')) ON CONFLICT DO NOTHING;
