INSERT INTO games (id, name, summary, release) VALUES (11412, 'Baseball', 'Baseball is a baseball sports game that was created on a PDP-10 mainframe computer at Pomona College in 1971 by student Don Daglow. The game (actually spelled BASBAL due to the 6-character file name length restrictions) continued to be enhanced periodically through 1976. The program is documented at the Baseball Hall of Fame in Cooperstown, New York. It was the first interactive Baseball simulation game, allowing players to manage the game as it unfolded.
INSERT INTO games (id, name, summary, release) VALUES (7582, 'Baseball', 'Baseball is a combined board-video game for the Magnavox Odyssey system that runs with its Cartridge No.3.', DATE('1972-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7580, 'Baseball', 'The simply titled game "Baseball" was developed by Nintendo in 1983 for the Nintendo Family Computer, and was one of the very first games released for the Famicon. It was also included as one of the 18 games released for the Nintendo Entertainment System''s original launch in the United States.', DATE('1983-12-06')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22827, 'Baseball 2000', null, null) ON CONFLICT DO NOTHING;
