INSERT INTO games (id, name, summary, release) VALUES (3420, 'Bass Masters 2000', null, DATE('1999-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5737, 'Bass Pro Shops Trophy Hunter 2007', 'Bass Pro Shops Trophy Hunter 2007 challenges you to test your hunting instincts with all-new challenges. Equip yourself with the latest hunting equipment including bows, rifles, shotguns, and defensive firearms and take to 10 real-life regions! Use hides, calls, scents, lures, and stands to bring your award-winning Trophy in for the perfect kill.', DATE('2006-11-19')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4706, 'Bass Pro Shops: The Strike', null, DATE('2009-10-05')) ON CONFLICT DO NOTHING;
