INSERT INTO games (id, name, summary, release) VALUES (12511, 'Battle Chasers: Nightwar', 'Battle Chasers: Nightwar is an RPG inspired by the classic console greats, featuring deep dungeon diving, turn-based combat presented in a classic JRPG format, and tons of secrets, story and randomly-generated replay goodness. The game is being created by ex-Vigil Games veterans, including two of Vigil''s co-founders, Joe Madureira and Ryan Stefanelli.
INSERT INTO games (id, name, summary, release) VALUES (23689, 'Battle Chef Brigade', '"Battle Chef Brigade features fantasy chefs, including Orcs, Elves, and Humans, who need to hunt dangerous monsters for ingredients. With those ingredients, before the critical eye of the judges, the chefs create culinary masterpieces in an intense tournament. Battle Chef Brigade pairs one part combo-based action hunting and one part puzzle-based cooking, all as the round clock ticks down."', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2603, 'Battle Chess', 'Battle Chess was developed by Interplay as their first project after severing ties with Electronic Arts. They designed it for the Amiga in 1988, and it was released on the majority of the other systems in the late 1980s and early 1990s as well. It was widely successful, and resulted in two follow-ups, many copy-cat games, and a remake for Steam on PC. The only significant criticism the game received was the weak chess AI, as it was not until the mid 1990s that chess ai began to consistently win against strong opponents.', DATE('1988-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11099, 'Battle Chess 4000', 'The Chess game with lots of animations and battle scenarios when you score.', DATE('1992-12-30')) ON CONFLICT DO NOTHING;
