INSERT INTO games (id, name, summary, release) VALUES (15152, 'Battle Chess: Game of Kings', 'It took 2,000 years for someone to make chess better!  The most challenging game on earth comes to life in Battle Chess. An entire medieval world at war is reflected on the checkered field.', DATE('2014-01-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4596, 'Battle City', 'Battle City (バトルシティー Batoru Shitī), also known as Tank 1990 or Tank in some pirate multicarts releases is a multi-directional shooter video game for the Family Computer produced and published in 1985 by Namco. The game was later released for the Game Boy and was included in the Japanese version of Star Fox: Assault. It is a port of the arcade game Tank Battalion with additional features (including two player simultaneous play, and an edit feature, both explained later). There was also a rendition for Nintendo''s Vs. System arcade cabinets.
INSERT INTO games (id, name, summary, release) VALUES (24651, 'Battle City Multiplayer', 'The classic 1985 Namco title where you can control up to two tank and must destroy all 20 enemies in 35 different stages, now with tcp/ip support to play over the internet with your friends.', null) ON CONFLICT DO NOTHING;
