INSERT INTO games (id, name, summary, release) VALUES (15013, 'BattleSouls', 'Join the battle for the legendary soul crystals in the fast-paced PvP action game where Class Change is King — BattleSouls! Freely switch between a loadout of three heroes of your choosing, each with his or her own unique abilities, to destroy the opposition’s soul crystals before they destroy yours in action-packed melee and ranged combat.', DATE('2016-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17530, 'BattleSpace', 'BattleSpace is a sci-fi flash game designed to play in the comfort of your web browser. In this free online strategy game, you step into the role of an Admiral, building a solar system in the depths of a faraway galaxy. Every player starts in their own ''Union'', or guild, with the option of banding together and creating an interstellar empire.
