INSERT INTO games (id, name, summary, release) VALUES (23845, 'Battlewang', 'A virtual cockfighting simulation and betting game.  Compete to have the biggest rooster, longest winning streak, etc.  Virtual corn.  Re-released after a 12 year hiatus.', DATE('2016-09-06')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12916, 'Battlezone', 'Battlezone is an arcade game from Atari released in November 1980. It displays a wireframe view (using vector graphics rather than raster graphics) on a horizontal black and white (with green and red sectioned color overlay) vector monitor. Due to its novel gameplay and look, this game was very popular for many years. Throughout the 1980s, Battlezone was ported to several home computer systems (usually on the Atarisoft label), including the Apple II, the Commodore 64, the Sinclair ZX Spectrum, and the Atari XEGS. The Atari ST port contains large parts of the original 6502 code which is emulated in real time. An Atari 2600 port was also released, but has colored raster graphics due to limitations and the view is behind the tank rather than inside it.', DATE('1980-10-31')) ON CONFLICT DO NOTHING;
