INSERT INTO games (id, name, summary, release) VALUES (10048, 'BlackBay Asylum', 'You take on the role of a confused psychopath who finds himself able to leave his cell one day. Explore the hospital grounds, solve mind shattering puzzles, survive relentless horrors and follow the story of Doug Dunheiw as he tries to find some clarity in what goes on at Blackbay Asylum.', DATE('2014-07-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5730, 'BlackSite: Area 51', 'Modern day fears explode onto the streets of a small American town swarming with alien life. The government is desparate, struggling to contain secrets so terrible they can no longer be kept. You are Aeran Pierce, a Special Forces assassin thrown into one of the most explosive moments in American history. Everything hinges on your actions in BlackSite: Area 51.', DATE('2007-11-11')) ON CONFLICT DO NOTHING;
