INSERT INTO games (id, name, summary, release) VALUES (13509, 'Boot Camp', 'Combat School (コンバットスクール Konbatto Sukūru?, distributed in North America as Boot Camp), is a 1987 arcade game produced by Konami where the player takes control of a military recruit who is undergoing basic training at a United States Marine Corps Recruit Training also known as a boot camp.', DATE('1987-04-01')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18608, 'Boot Hill', 'Boot Hill is a shooter arcade game released by Midway in 1977. The game is a sequel to Western Gun (aka Gun Fight). It''s a classic one or two-player western gunfight game. Each player uses a small joystick to move their cowboy up and down the play area, while a second, much larger joystick is used to aim the pistol and shoot - this larger stick also has a trigger button. The game''s single goal is simply to shoot the other player, who is situated on the opposite side of the game area. Wagons and cacti litter the middle of the play area, providing temporary cover from the opponent''s gunfire. These obstacles slowly disintegrate as they are shot.', DATE('1977-12-30')) ON CONFLICT DO NOTHING;
