INSERT INTO games (id, name, summary, release) VALUES (18216, 'Cadence', 'You''ve been playing games most of your life. You''ve been listening to music even longer. Wouldn''t it be great if making music was as easy as playing a game? Cadence lets you draw shapes as music. Let your imagination run free with the PC''s first music sandbox game.', null) ON CONFLICT DO NOTHING;
