INSERT INTO games (id, name, summary, release) VALUES (17927, 'CaesarIA', 'CaesarIA is inspired by an old-school economic strategy game, Caesar III which is a masterpiece among the games of this genre in our opinion. There are different economic strategy games and they can be broken down to subtypes but from our viewpoint Caesar encompasses planning, constructing, diplomacy and military campaigns.', DATE('2016-01-19')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4597, 'Caesars Palace', 'Caesars Palace is a 1992 Virgin Interactive gambling video game developed by Realtime Associates for the Nintendo Entertainment System and Magnin and Associates for the Game Boy; also there is Game Gear (1993) version.', DATE('1991-12-30')) ON CONFLICT DO NOTHING;
