INSERT INTO games (id, name, summary, release) VALUES (1122, 'Call of Duty: Black Ops II', 'Black Ops II is the first game in the Call of Duty franchise to feature future warfare technology and the first to present branching storylines driven by player choice as well as selecting weapons before starting story mode missions.', DATE('2012-11-12')) ON CONFLICT DO NOTHING;
