INSERT INTO games (id, name, summary, release) VALUES (17017, 'Canyon Bomber', 'Canyon Bomber is a black-and-white 1978 arcade game, developed and published by Atari. The game was rewritten in color and with a different visual style for the Atari 2600, also in 1978. The player and an opponent fly a blimp or biplane over a canyon full of numbered, circular rocks, arranged in layers. The player does not control the flight of vehicles, but only presses a button to drop bombs which destroy rocks and give points. Each rock is labeled with the points given for destroying it. As the number of rocks is reduced, it becomes harder to hit them without missing. The third time a player drops a bomb without hitting a rock, the game is over.', DATE('1978-12-30')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7875, 'Canyon Capers', 'A challenging retro style platformer game with a multitude of levels and a variety of interesting mechanics. Explore the worlds, fight the enemies, collect the bonuses and find all the secrets!', DATE('2014-04-24')) ON CONFLICT DO NOTHING;
