INSERT INTO games (id, name, summary, release) VALUES (15210, 'Checkmate', 'Checkmate is the most powerful and versatile chess program on the market with state-of-the-art graphics, a highly intuitive user interface, and the widest range of features found on any micro-computer chess program, many of which have never before been implemented on a personal computer.', DATE('1987-12-30')) ON CONFLICT DO NOTHING;
