INSERT INTO games (id, name, summary, release) VALUES (6936, 'Child of Eden', 'Child of Eden is the “multi-sensory shooter” that will send players diving into a kaleidoscopic matrix of synchronized music and mind-blowing visuals that will usher forth yet another landmark game experience from the mind of renowned game designer Tetsuya Mizuguchi, its creator.', DATE('2011-06-13')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4756, 'Child of Light', 'The Black Queen has stolen the Sun, the Moon and the Stars. You play as Aurora, a young princess with a pure heart whose soul is brought to the kingdom of Lemuria. Embark on a quest to recapture the three sources of light, defeat the Black Queen and restore the kingdom of Lemuria.', DATE('2014-04-28')) ON CONFLICT DO NOTHING;
