INSERT INTO games (id, name, summary, release) VALUES (8425, 'Chompy Chomp Chomp', 'Chompy Chomp Chomp is a maze-based multiplayer rampage in which Chompies chase each other around trying to eat their target colour Chompy. Underneath your Chompy''s feet you have a target the colour of the Chompy you need to eat. You get points for chomping, you lose points for being chomped. The target colours change at random, turning hunters into hunted and vice versa.', DATE('2012-05-23')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18252, 'Chop Sushi', 'Get caught up in sushi-matching mayhem with this innovative puzzler with hours of match 3 puzzle action as you conquer 40 challenging puzzle battles. Create unique combinations, triggering insane combos including the awesome WASABI BOMB! and unleash their devastating effects on your opponents!', DATE('2010-07-27')) ON CONFLICT DO NOTHING;
