INSERT INTO games (id, name, summary, release) VALUES (1802, 'Chrono Trigger', 'When a newly developed teleportation device malfunctions at the Millennial Fair, young Crono must travel through time to rescue his misfortunate companion from an intricate web of past and present perils. The swashbuckling adventure that ensues soon unveils an evil force set to destroy the world, triggering Crono''s race against time to change the course of history and bring about a brighter future.', DATE('1995-03-10')) ON CONFLICT DO NOTHING;
