INSERT INTO games (id, name, summary, release) VALUES (10087, 'Chronology', 'Chronology is a mind-bending mix of puzzle, adventure and platform game where you defy time by manipulating the past and the future, in order to fix the present. Play as the Old Inventor and his sidekick The Snail, and take advantage of their special abilities - travel back and forth in time, stop time, manipulate objects and solve puzzles.', null) ON CONFLICT DO NOTHING;
