INSERT INTO games (id, name, summary, release) VALUES (26141, 'Cities Skyline - Natural Disasters', 'Natural Disasters features a catalog of catastrophes to challenge mayor-players everywhere, including planning with early warning systems and emergency routes, devastating and destructive disaster effects, and caring for the populace as they struggle to rebuild. The expansion will also update Cities: Skylines with a new scenario editor and gameplay mode, allowing players to finally win – or lose – the game on their own terms.
INSERT INTO games (id, name, summary, release) VALUES (4212, 'Cities XL', 'Cities XL is a simulation game in which players can build their own cities on realistic 3D maps. Players are challenged to create the right combination of social services, employment, activities, and job opportunities for the cities'' citizens. In a handful of brief animations, a grandmother character will get into scuffles with much stronger men; this is usually depicted in a slapstick manner as the men cower in fear or futilely strike back.', DATE('2009-10-08')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2154, 'Cities XL 2011', null, DATE('2010-10-13')) ON CONFLICT DO NOTHING;
