INSERT INTO games (id, name, summary, release) VALUES (27546, 'City Climber', 'City Climber is a silly physics-based game about climbing and finding your way through various environments. Progress through the story or enjoy funny challenges filled with falling and destruction.', DATE('2017-02-23')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4610, 'City Connection', 'City Connection (シティコネクション Shiti Konekushon) is a video game developed and published in 1985 by Jaleco for the arcade in Japan and published by Kitcorp as Cruisin'' outside of Japan.
INSERT INTO games (id, name, summary, release) VALUES (22011, 'City Crisis', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15719, 'City Life 2008', 'Construct the city of the 21st Century with City Life! City Life 2008 Edition is a next-generation City Builder. Its 3D game engine provides a level of detail and animation unprecedented in the City Builder genre, with total freedom of construction. From superb villas in residential neighbourhoods to imposing skyscrapers in the financial district, design, construct and develop the city of your dreams. Provide comfort and satisfaction for your citizens by offering them work, health care, entertainment facilities, and much more, and ensure that your city’s coffers are brimming. It’s up to you to manage your city efficiently, balancing booming development with the problems of pollution and traffic that this brings. Build a sense of community between the six unique subcultures, or pay the price if their relationships deteriorate into conflict or riots. You are the architect, mayor and business mogul!', DATE('2008-07-31')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17565, 'City Quest', 'Welcome back the past with City Quest, a powerful love letter to the adventure games of yesteryear. Follow a simple farmer boy as he tries to make a name for himself in the big city. Players can enjoy four storylines - Hobo, Mafia, Politician, and Lady of the Night, each with their own unique cast of misfits to help you along the way (as well as several different endings for each). Packed with often offensive and always absurd humor, this game will tickle the funnybone and anger the gods.', DATE('2015-07-03')) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25497, 'City Rain', null, null) ON CONFLICT DO NOTHING;
