INSERT INTO games (id, name, summary, release) VALUES (19056, 'City Z', 'The virus appeared just six months ago and has already devastated humanity as we knew it. Governments fell one after another. Only a few decimated and isolated groups remain, trying to survive and free their regions from the poison.', null) ON CONFLICT DO NOTHING;
