INSERT INTO games (id, name, summary, release) VALUES (25412, 'Clever Kids Pony World', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15563, 'Clicker Heroes', 'Ever wondered what one quadrillion damage per second feels like? Wonder no more! Embark on your quest to attain it today! Start out by clicking on the monster to kill them, and get their gold. Spend that gold on hiring new heroes and get more damage. The more damage you deal, the more gold you will get.', DATE('2014-06-30')) ON CONFLICT DO NOTHING;
